import React, { useEffect, useState } from 'react'
import MovieBox from './MovieBox';
import axios from 'axios';


const API_URL = "https://api.tvmaze.com/search/shows?q=all"


function App() {

  const [movies,setMovies] = useState([]);

  // useEffect(()=>{
