  //       .then(data =>{
  //         console.log(data)
  //         setMovies(data)
  //         console.warn(movies);
  //         })
  //       },[])

  useEffect(()=>{
      axios.get("https://api.tvmaze.com/search/shows?q=all")
      .then(res => setMovies(res.data))
       .catch(err => console.log(err))
  },[])


  return (
    // <div>
