    //   }
       <div>
         <table>
           <thead>
             <tr>ID</tr>
           </thead>
           <tbody>
             { movies.map((r,i)=>(
               <tr key={i}>{r.show}</tr>
             ))

           }
          </tbody>
        </table>
     </div> 


  )
}

export default App
