import React, { useEffect, useState } from 'react'
import MovieBox from './MovieBox';
const API_URL = "https://api.tvmaze.com/search/shows?q=all"


function App() {

  const [movies,setMovies] = useState(["d"]);

  useEffect(()=>{
        fetch(API_URL)
        .then((res)=>res.json())
        .then(data =>{
          console.log(data)
          setMovies(data)
          })
        },[])
  return (
    <div>
      {
        movies.map((movieReq)=> 

      <MovieBox key={movieReq.id} {...movieReq}/>)
      }
    </div>
  )
}

export default App
