import React, { useState } from 'react'
import { useLocation } from 'react-router-dom'




function FromForTicketBoiking() {
      const DefaultValueLocation = useLocation();
     const[email ,setEmail] = useState("");
     const[password, setPassword] = useState("");
     const[showname, setShowname] = useState("");
     const[showid, setshowId] = useState("");
     const[userAge, setUserAge] = useState("");


   //   function handler(){
   //        localStorage.setItem("email",email)
   //        localStorage.setItem("password",password)
   //        localStorage.setItem("Age",userAge)
   //       // localStorage.setItem("ID",showid)
   //       // localStorage.setItem("Show-Name",showname)

   //   }



  return (
     <div className='Form_box-ticket-booking'>
     
        <form action=" " >
        <div className='from-field-container'>
         <div id='user-detail-box'>
         

      
            <label htmlFor='email'>Email</label>
            <input type='text' name='email' id='email'  onChange={(e)=>setEmail(e.target.value)}/>


            <label htmlFor='age'> Age</label>
           <input type='number' id='age' onChange={(e)=>setUserAge(e.target.value)}/>


            <label htmlFor='password' >Password</label>
            <input type='text' name='password' id='password' onChange={(e)=>setPassword(e.target.value)} />
           
         
             <label htmlFor='show' >Show Name:</label>
             <input type='text' id='show' value={DefaultValueLocation.state.showName}  />



             <label htmlFor='time'>Show-ID:</label>
             <input type="text" id='time' value={DefaultValueLocation.state.id_show} />

      <button  type="submit" style={{margin:"10px"}}>BOOK TICKET</button>
    </div>
       </div>
        </form>
   
     </div>
  )
}

export default FromForTicketBoiking
